pragma solidity =0.8.13;

import "@openzeppelin/contracts-upgradeable/utils/math/MathUpgradeable.sol";

struct UnpackedRemovalIdV0 {
  uint8 idVersion;
  uint8 methodology;
  uint8 methodologyVersion;
  uint16 vintage;
  bytes2 country;
  bytes2 subdivision;
  address supplierAddress;
  uint32 subIdentifier;
}

uint256 constant _BITS_PER_BYTE = 8;
uint256 constant _ENCODED_LENGTH_BYTES_UNPACKED_ID_V0 = 256;

uint256 constant _ID_VERSION_FIELD_LENGTH = 1;
uint256 constant _METHODOLOGY_DATA_FIELD_LENGTH = 1;
uint256 constant _VINTAGE_FIELD_LENGTH = 2;
uint256 constant _COUNTRY_CODE_FIELD_LENGTH = 2;
uint256 constant _ADMIN1_CODE_FIELD_LENGTH = 2;
uint256 constant _ADDRESS_FIELD_LENGTH = 20;
uint256 constant _SUBID_FIELD_LENGTH = 4;

uint256 constant _ID_VERSION_OFFSET = 31;
uint256 constant _METHODOLOGY_DATA_OFFSET = 30;
uint256 constant _VINTAGE_OFFSET = 28;
uint256 constant _COUNTRY_CODE_OFFSET = 26;
uint256 constant _ADMIN1_CODE_OFFSET = 24;
uint256 constant _ADDRESS_OFFSET = 4;
uint256 constant _SUBID_OFFSET = 0;

uint256 constant _ASCII_CAP_LETTER_MIN_VAL = 65;
uint256 constant _ASCII_CAP_LETTER_MAX_VAL = 90;

/**
 * @dev Library encapsulating the logic around encoding and decoding removal token ids.
 *
 * The token ids used for a given ERC1155 token in Removal.sol encode information about the carbon removal
 * in the following format(s), where the first byte encodes the format version:
 *
 * Version 0:
 * [1byte][1byte][--2 bytes--][--2 bytes--][--2 bytes--][----------- 20 bytes------------- ][------4 bytes------]
 * tokIdV--meth&v---vintage------country------subdivision------------ supplier address --------------subidentifier--
 *
 * For methodology 1 (regenerative ag), the subidentifier serves as a parcel identifier.
 *
 */
library RemovalUtils {
  /**
   * @notice Packs data about a removal into a 256-bit token id for the removal.
   * @dev Performs some possible validations on the data before attempting to create the id.
   * @param removalData removal data encoded as bytes, with the first byte storing the version.
   */
  function createRemovalId(bytes calldata removalData)
    internal
    pure
    returns (uint256)
  {
    uint256 idVersion = abi.decode(removalData, (uint8));
    require(idVersion == 0, "Unsupported removal token id version");
    require(
      removalData.length == _ENCODED_LENGTH_BYTES_UNPACKED_ID_V0,
      "removalData contains wrong number of bytes"
    );

    UnpackedRemovalIdV0 memory params = abi.decode(
      removalData,
      (UnpackedRemovalIdV0)
    );

    require(params.methodology <= 2**4 - 1, "Metholodogy too large");
    require(
      params.methodologyVersion <= 2**4 - 1,
      "Metholodogy version too large"
    );

    uint256 countryFirstLetter = uint256(uint16(params.country)) >>
      _BITS_PER_BYTE;
    uint256 countrySecondLetter = uint256(uint16(params.country)) &
      uint256(2**8 - 1);
    uint256 subdivisionFirstLetter = uint256(uint16(params.subdivision)) >>
      _BITS_PER_BYTE;
    uint256 subdivisionSecondLetter = uint256(uint16(params.subdivision)) &
      uint256(2**8 - 1);

    require(
      countryFirstLetter >= _ASCII_CAP_LETTER_MIN_VAL &&
        countryFirstLetter <= _ASCII_CAP_LETTER_MAX_VAL &&
        countrySecondLetter >= _ASCII_CAP_LETTER_MIN_VAL &&
        countrySecondLetter <= _ASCII_CAP_LETTER_MAX_VAL &&
        subdivisionFirstLetter >= _ASCII_CAP_LETTER_MIN_VAL &&
        subdivisionFirstLetter <= _ASCII_CAP_LETTER_MAX_VAL &&
        subdivisionSecondLetter >= _ASCII_CAP_LETTER_MIN_VAL &&
        subdivisionSecondLetter <= _ASCII_CAP_LETTER_MAX_VAL,
      "Invalid ASCII"
    );

    uint256 methodologyData = (params.methodology << 4) |
      params.methodologyVersion;

    return
      (uint256(params.idVersion) << (_ID_VERSION_OFFSET * _BITS_PER_BYTE)) |
      (uint256(methodologyData) <<
        (_METHODOLOGY_DATA_OFFSET * _BITS_PER_BYTE)) |
      (uint256(params.vintage) << (_VINTAGE_OFFSET * _BITS_PER_BYTE)) |
      (uint256(uint16(params.country)) <<
        (_COUNTRY_CODE_OFFSET * _BITS_PER_BYTE)) |
      (uint256(uint16(params.subdivision)) <<
        (_ADMIN1_CODE_OFFSET * _BITS_PER_BYTE)) |
      (uint256(uint160(params.supplierAddress)) <<
        (_ADDRESS_OFFSET * _BITS_PER_BYTE)) |
      (uint256(params.subIdentifier) << (_SUBID_OFFSET * _BITS_PER_BYTE));
  }

  /**
   * @notice Unpacks a V0 removal id into its component data.
   */
  function unpackRemovalIdV0(uint256 removalId)
    internal
    pure
    returns (UnpackedRemovalIdV0 memory)
  {
    return
      UnpackedRemovalIdV0(
        version(removalId),
        methodology(removalId),
        methodologyVersion(removalId),
        vintage(removalId),
        countryCode(removalId),
        subdivisionCode(removalId),
        supplierAddress(removalId),
        subIdentifier(removalId)
      );
  }

  /**
   * @notice Extracts and returns the version field of a removal token id.
   */
  function version(uint256 removalId) internal pure returns (uint8) {
    return
      uint8(
        _extractValue(removalId, _ID_VERSION_FIELD_LENGTH, _ID_VERSION_OFFSET)
      );
  }

  /**
   * @notice Extracts and returns the methodology field of a removal token id.
   */
  function methodology(uint256 removalId) internal pure returns (uint8) {
    return
      uint8(
        _extractValue(
          removalId,
          _METHODOLOGY_DATA_FIELD_LENGTH,
          _METHODOLOGY_DATA_OFFSET
        ) >> 4
      ); // methodology encoded in the first nibble
  }

  /**
   * @notice Extracts and returns the methodology version field of a removal token id.
   */
  function methodologyVersion(uint256 removalId) internal pure returns (uint8) {
    return
      uint8(
        _extractValue(
          removalId,
          _METHODOLOGY_DATA_FIELD_LENGTH,
          _METHODOLOGY_DATA_OFFSET
        ) & (2**4 - 1)
      ); // methodology version encoded in the second nibble
  }

  /**
   * @notice Extracts and returns the vintage field of a removal token id.
   */
  function vintage(uint256 removalId) internal pure returns (uint16) {
    return
      uint16(_extractValue(removalId, _VINTAGE_FIELD_LENGTH, _VINTAGE_OFFSET));
  }

  /**
   * @notice Extracts and returns the country code field of a removal token id.
   */
  function countryCode(uint256 removalId) internal pure returns (bytes2) {
    return
      bytes2(
        uint16(
          _extractValue(
            removalId,
            _COUNTRY_CODE_FIELD_LENGTH,
            _COUNTRY_CODE_OFFSET
          )
        )
      );
  }

  /**
   * @notice Extracts and returns the subdivision field of a removal token id.
   */
  function subdivisionCode(uint256 removalId) internal pure returns (bytes2) {
    return
      bytes2(
        uint16(
          _extractValue(
            removalId,
            _ADMIN1_CODE_FIELD_LENGTH,
            _ADMIN1_CODE_OFFSET
          )
        )
      );
  }

  /**
   * @notice Extracts and returns the supplier address field of a removal token id.
   */
  function supplierAddress(uint256 removalId) internal pure returns (address) {
    return
      address(
        uint160(
          _extractValue(removalId, _ADDRESS_FIELD_LENGTH, _ADDRESS_OFFSET)
        )
      );
  }

  /**
   * @notice Extracts and returns the subIdentifier field of a removal token id.
   */
  function subIdentifier(uint256 removalId) internal pure returns (uint32) {
    return uint32(_extractValue(removalId, _SUBID_FIELD_LENGTH, _SUBID_OFFSET));
  }

  /**
   * @dev Extracts a field of the specified length in bytes, at the specified location, from a removal id.
   */
  function _extractValue(
    uint256 removalId,
    uint256 numBytesFieldLength,
    uint256 numBytesOffsetFromRight
  ) private pure returns (uint256) {
    bytes32 mask = bytes32(2**(numBytesFieldLength * _BITS_PER_BYTE) - 1) <<
      (numBytesOffsetFromRight * _BITS_PER_BYTE);
    bytes32 maskedValue = bytes32(removalId) & mask;
    return uint256(maskedValue >> (numBytesOffsetFromRight * _BITS_PER_BYTE));
  }
}
"},"contracts/Removal.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity =0.8.13;

import "@openzeppelin/contracts-upgradeable/token/ERC1155/presets/ERC1155PresetMinterPauserUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC1155/extensions/ERC1155SupplyUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC777/IERC777RecipientUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC777/ERC777Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC777/IERC777RecipientUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/introspection/ERC1820ImplementerUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/math/SafeMathUpgradeable.sol";
import {RemovalUtils, UnpackedRemovalIdV0} from "./RemovalUtils.sol";

// import "hardhat/console.sol"; // todo

// todo non-transferable/approveable after mint (except by DEFAULT_ADMIN_ROLE)
// todo disable other mint functions

/**
 * @title Removal
 */
contract Removal is
  ERC1155PresetMinterPauserUpgradeable,
  ERC1155SupplyUpgradeable
{
  using SafeMathUpgradeable for uint256;
  using RemovalUtils for uint256;

  struct BatchMintRemovalsData {
    address marketAddress;
    bool list;
  }

  uint256 private _tokenIdCounter;
  string public name; // todo why did I add this
  mapping(uint256 => uint256) public indexToTokenId; // todo consider how we're keeping track of the number and order of ids, ability to iterate
  mapping(uint256 => bool) private _tokenIdExists;

  function initialize() public virtual initializer {
    super.initialize("https://nori.com/api/removal/{id}.json");
    __ERC1155Supply_init_unchained();
    _tokenIdCounter = 0;
    name = "Removal";
  }

  /**
   * @dev See {IERC1155-setApprovalForAll}.
   */
  function setApprovalForAll(
    address owner,
    address operator,
    bool approved
  ) public virtual onlyRole(DEFAULT_ADMIN_ROLE) whenNotPaused {
    _setApprovalForAll(owner, operator, approved);
  }

  /**
   * @notice Packs data about a removal into a 256-bit token id for the removal.
   * @dev Performs some possible validations on the data before attempting to create the id.
   * @param removalData removal data encoded as bytes, with the first byte storing the version.
   */
  function createRemovalId(bytes calldata removalData)
    public
    pure
    returns (uint256)
  {
    return RemovalUtils.createRemovalId(removalData);
  }

  /**
   * @notice Unpacks a V0 removal id into its component data.
   */
  function unpackRemovalIdV0(uint256 removalId)
    public
    pure
    returns (UnpackedRemovalIdV0 memory)
  {
    return removalId.unpackRemovalIdV0();
  }

  /**
   * @dev mints multiple removals at once (for a single supplier).
   * If `list` is true in the decoded BatchMintRemovalsData, also lists those removals for sale in the market.
   * amounts: [100 * (10 ** 18), 10 * (10 ** 18), 50 * (10 ** 18)] <- 100 tonnes, 10 tonnes, 50 tonnes in standard erc20 units (wei)
   * token id 0 URI points to vintage information (e.g., 2018) nori.com/api/removal/0 -> { amount: 100, supplier: 1, vintage: 2018, ... }
   * token id 1 URI points to vintage information (e.g., 2019) nori.com/api/removal/1 -> { amount: 10, supplier: 1, vintage: 2019, ... }
   * token id 2 URI points to vintage information (e.g., 2020) nori.com/api/removal/2 -> { amount: 50, supplier: 1, vintage: 2020, ... }
   * @param to The supplier address
   * @param amounts Each removal's tonnes of CO2 formatted as wei
   * @param ids The token ids to use for this batch of removals. The id itself encodes the supplier's ethereum address, a parcel identifier,
   * the vintage, country code, state code, methodology identifer, and methodology version.
   * @param data Encodes the market contract address and a unique identifier for the parcel from whence these removals came.
   */
  function mintBatch(
    address to,
    uint256[] memory amounts,
    uint256[] memory ids,
    bytes memory data
  ) public override {
    BatchMintRemovalsData memory decodedData = abi.decode(
      data,
      (BatchMintRemovalsData)
    );
    for (uint256 i = 0; i < ids.length; i++) {
      require(!_tokenIdExists[ids[i]], "Token id already exists"); // todo can the duplicate token id be reported here?

      _tokenIdExists[ids[i]] = true;
      indexToTokenId[_tokenIdCounter] = ids[i];
      _tokenIdCounter += 1;
    }
    super.mintBatch(to, ids, amounts, data);

    setApprovalForAll(to, _msgSender(), true); // todo look at vesting contract for potentially better approach
    if (decodedData.list) {
      safeBatchTransferFrom(to, decodedData.marketAddress, ids, amounts, data);
    }
  }

  /**
   * @dev used to initiate a sale of removals by transferring the removals to the market contract
   */
  function safeBatchTransferFrom(
    address _from,
    address _to,
    uint256[] memory _ids,
    uint256[] memory _amounts,
    bytes memory _data
  ) public override {
    // todo require _to is a known market contract
    super.safeBatchTransferFrom(_from, _to, _ids, _amounts, _data);
  }

  function supportsInterface(bytes4 interfaceId)
    public
    view
    override(ERC1155Upgradeable, ERC1155PresetMinterPauserUpgradeable)
    returns (bool)
  {
    return super.supportsInterface(interfaceId);
  }

  function _beforeTokenTransfer(
    address operator,
    address from,
    address to,
    uint256[] memory ids,
    uint256[] memory amounts,
    bytes memory data
  )
    internal
    override(ERC1155PresetMinterPauserUpgradeable, ERC1155SupplyUpgradeable)
  {
    return super._beforeTokenTransfer(operator, from, to, ids, amounts, data);
  }
}
"},"contracts/Certificate.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity =0.8.13;

import "@openzeppelin/contracts-upgradeable/token/ERC1155/presets/ERC1155PresetMinterPauserUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC1155/extensions/ERC1155SupplyUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC777/IERC777RecipientUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC777/ERC777Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC777/IERC777RecipientUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/introspection/ERC1820ImplementerUpgradeable.sol";

// import "hardhat/console.sol"; // todo

// todo non-transferable/approveable
// todo disable other mint functions

/**
 * @title Certificate
 */
contract Certificate is
  ERC1155PresetMinterPauserUpgradeable,
  ERC1155SupplyUpgradeable
{
  struct Source {
    uint256 removalId;
    uint256 amount;
  }

  /**
   * @dev a mapping of the certificate token ID -> sources
   */
  mapping(uint256 => Source[]) private _sources;
  /**
   * @dev auto incrementing token ID
   */
  uint256 private _latestTokenId;

  function initialize() public virtual initializer {
    super.initialize("https://nori.com/api/certificate/{id}.json");
    __ERC1155Supply_init_unchained();
    _latestTokenId = 0;
  }

  function addMinter(address _minter) public {
    require(
      hasRole(DEFAULT_ADMIN_ROLE, _msgSender()),
      "Certificate: missing minter role"
    );
    _setupRole(MINTER_ROLE, _minter);
  }

  /**
   * @dev mints the certificate (using a batch of certificate sources)
   * @param to The supplier address
   * @param removalAmounts the removal source amount
   * @param removalIds the removal source ids
   * @param data Additional data with no specified format, MUST be sent
   * unaltered in call to `onERC1155Received` on `_to`
   *
   * @custom:example mint(address, 160, [100, 10, 50], [0, 1, 2])
   *  - token id 0 URI points to nori.com/api/certificate/0 -> { amount: 100, removalIds: [0, 1, 2], ... }
   *  - removalIds can be used to look up vintage years, e.g. 0 -> 2018
   */
  function mintBatch(
    address to,
    uint256[] memory removalIds,
    uint256[] memory removalAmounts,
    bytes memory data
  ) public override {
    uint256 certificateAmount = abi.decode(data, (uint256));
    // todo use modified mintCertificate instead of mintBatch. mintBatch should be used to mint multi certificates.
    // todo only allowed by market contract
    // todo require _sources[_latestTokenId] doesnt exist
    // todo require _sources[_latestTokenId][n] doesnt exist
    for (uint256 i = 0; i < removalIds.length; i++) {
      if (removalAmounts[i] == 0) {
        revert("Certificate: Removal amount 0");
      } else {
        // todo try filtering out the zero amountsbefore calling mint; revert if any are zero
        _sources[_latestTokenId].push(
          Source({removalId: removalIds[i], amount: removalAmounts[i]})
        );
      }
    }
    super.mint(to, _latestTokenId, certificateAmount, data);
    _latestTokenId = _latestTokenId += 1;
  }

  /**
   * @dev returns the removal IDs and the amounts of the sources
   */
  function sources(uint256 certificateId)
    public
    view
    returns (Source[] memory)
  {
    return _sources[certificateId];
  }

  function _beforeTokenTransfer(
    address operator,
    address from,
    address to,
    uint256[] memory ids,
    uint256[] memory amounts,
    bytes memory data
  )
    internal
    override(ERC1155PresetMinterPauserUpgradeable, ERC1155SupplyUpgradeable)
  {
    return super._beforeTokenTransfer(operator, from, to, ids, amounts, data);
  }

  function supportsInterface(bytes4 interfaceId)
    public
    view
    override(ERC1155Upgradeable, ERC1155PresetMinterPauserUpgradeable)
    returns (bool)
  {
    return super.supportsInterface(interfaceId);
  }
}
"},"contracts/ERC777PresetPausablePermissioned.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity =0.8.13;

import "@openzeppelin/contracts-upgradeable/token/ERC777/ERC777Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlEnumerableUpgradeable.sol";

contract ERC777PresetPausablePermissioned is
  ERC777Upgradeable,
  PausableUpgradeable,
  AccessControlEnumerableUpgradeable
{
